import * as m3o from "@m3o/m3o-node";
export class BitcoinService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Get the BTC balance of an address
    balance(request) {
        return this.client.call("bitcoin", "Balance", request);
    }
    // Get details for a bitcoin address
    lookup(request) {
        return this.client.call("bitcoin", "Lookup", request);
    }
    // Get the price of bitcoin
    price(request) {
        return this.client.call("bitcoin", "Price", request);
    }
    // Get transaction details by hash
    transaction(request) {
        return this.client.call("bitcoin", "Transaction", request);
    }
}
