import * as m3o from "@m3o/m3o-node";
export class ChatService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a new group
    create(request) {
        return this.client.call("chat", "Create", request);
    }
    // Delete a group
    delete(request) {
        return this.client.call("chat", "Delete", request);
    }
    // List the messages in a chat
    history(request) {
        return this.client.call("chat", "History", request);
    }
    // Invite a user to a group
    invite(request) {
        return this.client.call("chat", "Invite", request);
    }
    // Join a group
    join(request) {
        return this.client.stream("chat", "Join", request);
    }
    // Kick a user from a group
    kick(request) {
        return this.client.call("chat", "Kick", request);
    }
    // Leave a group
    leave(request) {
        return this.client.call("chat", "Leave", request);
    }
    // List available chats
    list(request) {
        return this.client.call("chat", "List", request);
    }
    // Connect to a chat to receive a stream of messages
    // Send a message to a chat
    send(request) {
        return this.client.call("chat", "Send", request);
    }
}
