import * as m3o from "@m3o/m3o-node";
export class CommentsService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a new comment
    create(request) {
        return this.client.call("comments", "Create", request);
    }
    // Delete a comment
    delete(request) {
        return this.client.call("comments", "Delete", request);
    }
    // Subscribe to comments events
    events(request) {
        return this.client.stream("comments", "Events", request);
    }
    // List all the comments
    list(request) {
        return this.client.call("comments", "List", request);
    }
    // Read a comment
    read(request) {
        return this.client.call("comments", "Read", request);
    }
    // Update a comment
    update(request) {
        return this.client.call("comments", "Update", request);
    }
}
