import * as m3o from "@m3o/m3o-node";
export class ContactService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a contact
    create(request) {
        return this.client.call("contact", "Create", request);
    }
    // Delete a contact
    delete(request) {
        return this.client.call("contact", "Delete", request);
    }
    // List contacts
    list(request) {
        return this.client.call("contact", "List", request);
    }
    // Read contact details
    read(request) {
        return this.client.call("contact", "Read", request);
    }
    // Update a contact
    update(request) {
        return this.client.call("contact", "Update", request);
    }
}
