import * as m3o from "@m3o/m3o-node";
export class DnsService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Query an addresss
    query(request) {
        return this.client.call("dns", "Query", request);
    }
    // Check who owns a domain
    whois(request) {
        return this.client.call("dns", "Whois", request);
    }
}
