import * as m3o from "@m3o/m3o-node";
export class EthereumService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Get the balance of an ethereum wallet
    balance(request) {
        return this.client.call("ethereum", "Balance", request);
    }
    // Broadcast presigned transaction to ethereum network
    broadcast(request) {
        return this.client.call("ethereum", "Broadcast", request);
    }
    // Get transaction details by hash
    transaction(request) {
        return this.client.call("ethereum", "Transaction", request);
    }
}
