import * as m3o from "@m3o/m3o-node";
export class ListsService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a new list
    create(request) {
        return this.client.call("lists", "Create", request);
    }
    // Delete a list
    delete(request) {
        return this.client.call("lists", "Delete", request);
    }
    // Subscribe to lists events
    events(request) {
        return this.client.stream("lists", "Events", request);
    }
    // List all the lists
    list(request) {
        return this.client.call("lists", "List", request);
    }
    // Read a list
    read(request) {
        return this.client.call("lists", "Read", request);
    }
    // Update a list
    update(request) {
        return this.client.call("lists", "Update", request);
    }
}
