import * as m3o from "@m3o/m3o-node";
export class NewsService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Get the latest news headlines
    headlines(request) {
        return this.client.call("news", "Headlines", request);
    }
    // Get the top stories
    topStories(request) {
        return this.client.call("news", "TopStories", request);
    }
}
