import * as m3o from "@m3o/m3o-node";
export class NftService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Get a single asset by the contract
    asset(request) {
        return this.client.call("nft", "Asset", request);
    }
    // Return a list of assets
    assets(request) {
        return this.client.call("nft", "Assets", request);
    }
    // Get a collection by its slug
    collection(request) {
        return this.client.call("nft", "Collection", request);
    }
    // Get a list of collections
    collections(request) {
        return this.client.call("nft", "Collections", request);
    }
    // Create your own NFT (coming soon)
    create(request) {
        return this.client.call("nft", "Create", request);
    }
}
