import * as m3o from "@m3o/m3o-node";
export class NotesService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a new note
    create(request) {
        return this.client.call("notes", "Create", request);
    }
    // Delete a note
    delete(request) {
        return this.client.call("notes", "Delete", request);
    }
    // Subscribe to notes events
    events(request) {
        return this.client.stream("notes", "Events", request);
    }
    // List all the notes
    list(request) {
        return this.client.call("notes", "List", request);
    }
    // Read a note
    read(request) {
        return this.client.call("notes", "Read", request);
    }
    // Update a note
    update(request) {
        return this.client.call("notes", "Update", request);
    }
}
