import * as m3o from "@m3o/m3o-node";
export class PingService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Ping an IP address
    ip(request) {
        return this.client.call("ping", "Ip", request);
    }
    // Ping a TCP port to check if it's open
    tcp(request) {
        return this.client.call("ping", "Tcp", request);
    }
    // Ping a HTTP URL
    url(request) {
        return this.client.call("ping", "Url", request);
    }
}
