import * as m3o from "@m3o/m3o-node";
export class PrayerService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Get the prayer (salah) times for a location on a given date
    times(request) {
        return this.client.call("prayer", "Times", request);
    }
}
