import * as m3o from "@m3o/m3o-node";
export class PriceService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Add a price
    add(request) {
        return this.client.call("price", "Add", request);
    }
    // Get the price of anything
    get(request) {
        return this.client.call("price", "Get", request);
    }
    // Get the index for available prices
    index(request) {
        return this.client.call("price", "Index", request);
    }
    // List prices for a given currency
    list(request) {
        return this.client.call("price", "List", request);
    }
    // Report an invalid price
    report(request) {
        return this.client.call("price", "Report", request);
    }
}
