import * as m3o from "@m3o/m3o-node";
export class QrService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // List your QR codes
    codes(request) {
        return this.client.call("qr", "Codes", request);
    }
    // Generate a QR code with a specific text and size
    generate(request) {
        return this.client.call("qr", "Generate", request);
    }
}
