import * as m3o from "@m3o/m3o-node";
export class SearchService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create an index by name
    createIndex(request) {
        return this.client.call("search", "CreateIndex", request);
    }
    // Delete an index by name
    deleteIndex(request) {
        return this.client.call("search", "DeleteIndex", request);
    }
    // Delete a record given its ID
    delete(request) {
        return this.client.call("search", "Delete", request);
    }
    // Index a record i.e. insert a document to search for.
    index(request) {
        return this.client.call("search", "Index", request);
    }
    // Search for records in a given in index
    search(request) {
        return this.client.call("search", "Search", request);
    }
}
