import * as m3o from "@m3o/m3o-node";
export class SecretService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Delete a secret. If key not found a success response is returned.
    delete(request) {
        return this.client.call("secret", "Delete", request);
    }
    // Get a secret by key.
    get(request) {
        return this.client.call("secret", "Get", request);
    }
    // List all the available secrets
    list(request) {
        return this.client.call("secret", "List", request);
    }
    // Set a secret. Overwrites any existing value already set.
    set(request) {
        return this.client.call("secret", "Set", request);
    }
}
