import * as m3o from "@m3o/m3o-node";
export class TwitterService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Search for tweets with a simple query
    search(request) {
        return this.client.call("twitter", "Search", request);
    }
    // Get the timeline for a given user
    timeline(request) {
        return this.client.call("twitter", "Timeline", request);
    }
    // Get the current global trending topics
    trends(request) {
        return this.client.call("twitter", "Trends", request);
    }
    // Get a user's twitter profile
    user(request) {
        return this.client.call("twitter", "User", request);
    }
}
