import * as m3o from "@m3o/m3o-node";
export class UrlService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a URL
    create(request) {
        return this.client.call("url", "Create", request);
    }
    // Delete a URL
    delete(request) {
        return this.client.call("url", "Delete", request);
    }
    // List all the shortened URLs
    list(request) {
        return this.client.call("url", "List", request);
    }
    // Resolve returns the destination URL of a short URL.
    resolve(request) {
        return this.client.call("url", "Resolve", request);
    }
    // Shorten a URL
    shorten(request) {
        return this.client.call("url", "Shorten", request);
    }
    // Update the destination for a short URL
    update(request) {
        return this.client.call("url", "Update", request);
    }
}
