import * as m3o from "@m3o/m3o-node";
export class WordleService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Make a guess
    guess(request) {
        return this.client.call("wordle", "Guess", request);
    }
    // When does the next game start
    next(request) {
        return this.client.call("wordle", "Next", request);
    }
}
